const web3 = new Web3('https://mainnet.infura.io/v3/YOUR_INFURA_PROJECT_ID'); const erc20ABI = [/* 这里粘贴上面的 ERC-20 ABI */];
const contractAddress = '0xTokenContractAddress'; // 替换为 ERC-20 代币合约地址 const erc20Contract = new web3.eth.Contract(erc20ABI, contractAddress); erc20Contract.methods.balanceOf('0xYourAddress').call() .then(balance => console.log(`Token Balance: ${balance}`));
