智能合约是以太坊最具革命性的特性，它们是部署在区块链上的自动执行程序。 定义 ：智能合约是一套以数字形式定义的承诺，包含了合约参与方可以执行的协议。 特点 ：自动执行、不可篡改、透明公开 语言 ：主要使用Solidity语言编写，也支持Vyper、Yul等 执行环境 ：以太坊虚拟机(EVM) 智能合约的代码示例(Solidity)： // 简单的代币合约示例
pragma solidity ^0.8.0; contract SimpleToken { string public name; string public symbol; uint256 public totalSupply; mapping(address => uint256) public balanceOf; event Transfer(address indexed from, address indexed to, uint256 value); constructor(string memory _name, string memory _symbol, uint256 _totalSupply) { name = _name; symbol = _symbol; totalSupply = _totalSupply; balanceOf[msg.sender] = _totalSupply; } function transfer(address _to, uint256 _value) public returns (bool success) { require(balanceOf[msg.sender] >= _value, "Insufficient balance"); balanceOf[msg.sender] -= _value; balanceOf[_to] += _value; emit Transfer(msg.sender, _to, _value); return true; }
}

